description:
function load_video {
	insmod efi_gop
	insmod efi_uga
	insmod video_bochs
	insmod video_cirrus
	insmod all_video
}

load_video
set gfxpayload=keep
insmod gzio

menuentry 'Install Fedora 64-bit' --class fedora --class gnu-linux --class gnu --class os {
	linuxefi f31/vmlinuz ip=dhcp inst.repo=https://download.fedoraproject.org/pub/fedora/linux/releases/31/Server/x86_64/os/
	initrdefi f31/initrd.img
}

menuentry 'Install Fedora 31 Server' --class fedora --class gnu-linux --class gnu --class os {
	kernel f31/vmlinuz
	append initrd=f31/initrd.img inst.repo=https://download.fedoraproject.org/pub/fedora/linux/releases/31/Server/x86_64/os/ ip=dhcp ks=https://git.fedorahosted.org/cgit/spin-kickstarts.git/plain/fedora-install-server.ks?h=f21
}
